namespace Zikula\TrivialModule\Base;

use Doctrine\DBAL\Connection;
use RuntimeException;
use Zikula\Core\AbstractExtensionInstaller;
use Zikula\CategoriesModule\Entity\CategoryRegistryEntity;

/**
 * Installer base class.
 */
abstract class AbstractTrivialModuleInstaller extends AbstractExtensionInstaller
{
    /**
     * Install the ZikulaTrivialModule application.
     *
     * @return boolean True on success, or false
     *
     * @throws RuntimeException Thrown if database tables can not be created or another error occurs
     */
    public function install()
    {
        $logger = $this->container->get('logger');
        $userName = $this->container->get('zikula_users_module.current_user')->get('uname');
    
        // create all tables from according entity definitions
        try {
            $this->schemaTool->create($this->listEntityClasses());
        } catch (\Exception $exception) {
            $this->addFlash('error', $this->__('Doctrine Exception') . ': ' . $exception->getMessage());
            $logger->error('{app}: Could not create the database tables during installation. Error details: {errorMessage}.', ['app' => 'ZikulaTrivialModule', 'errorMessage' => $exception->getMessage()]);
    
            return false;
        }
    
        // set up all our vars with initial values
        $this->setVar('tournamentEntriesPerPage', 10);
        $this->setVar('linkOwnTournamentsOnAccountPage', true);
        $this->setVar('questionEntriesPerPage', 10);
        $this->setVar('linkOwnQuestionsOnAccountPage', true);
        $this->setVar('answerEntriesPerPage', 10);
        $this->setVar('linkOwnAnswersOnAccountPage', true);
        $this->setVar('resultEntriesPerPage', 10);
        $this->setVar('linkOwnResultsOnAccountPage', true);
        $this->setVar('enabledFinderTypes', 'question');
    
        $categoryRegistryIdsPerEntity = [];
    
        // add default entry for category registry (property named Main)
        $categoryHelper = new \Zikula\TrivialModule\Helper\CategoryHelper(
            $this->container->get('translator.default'),
            $this->container->get('request_stack'),
            $logger,
            $this->container->get('zikula_users_module.current_user'),
            $this->container->get('zikula_categories_module.category_registry_repository'),
            $this->container->get('zikula_categories_module.api.category_permission')
        );
        $categoryGlobal = $this->container->get('zikula_categories_module.category_repository')->findOneBy(['name' => 'Global']);
        $entityManager = $this->container->get('doctrine.orm.default_entity_manager');
    
        $registry = new CategoryRegistryEntity();
        $registry->setModname('ZikulaTrivialModule');
        $registry->setEntityname('QuestionEntity');
        $registry->setProperty($categoryHelper->getPrimaryProperty('Question'));
        $registry->setCategory($categoryGlobal);
    
        try {
            $entityManager->persist($registry);
            $entityManager->flush();
        } catch (\Exception $exception) {
            $this->addFlash('error', $this->__f('Error! Could not create a category registry for the %entity% entity.', ['%entity%' => 'question']));
            $logger->error('{app}: User {user} could not create a category registry for {entities} during installation. Error details: {errorMessage}.', ['app' => 'ZikulaTrivialModule', 'user' => $userName, 'entities' => 'questions', 'errorMessage' => $exception->getMessage()]);
        }
        $categoryRegistryIdsPerEntity['question'] = $registry->getId();
    
        // initialisation successful
        return true;
    }
    
